tinymce.PluginManager.add('linknonpublic', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('linknonpublic', {
        text: 'Seiten',
        icon: false,
        onclick: function() {
           
            var selection = editor.selection.getContent({format: 'text'});
            
            
            
            // Open window
            editor.windowManager.open({
                title: 'linknonpublic plugin',
                body: [
                    
                    {type: 'textbox', name: 'label', label: 'Linkname', value : selection},
                    {
                    	type : 'listbox', name: 'cmspages', label: 'Seiten',
                    	values : [{text: 'None', value: ''},
                    	          
                    	          {text: 'Startseite', value: '/'},
                    	          {text: 'Kontakt', value: '/kontakt'},
                    	          ],
                    },
                    {type: 'textbox', name: 'title', label: 'Linktitle'},
                ],
                onsubmit: function(e) {
                	var str = '';
                	if (e.data.cmspages){
                		str = '<a href="' + e.data.cmspages + '"';
                		if (e.data.title){
                			str += ' title="' + e.data.title + '"';
                		}
                		str += '>' + e.data.label + '</a>';
                	}
                	
                	
                    // Insert content when the window form is submitted
                    editor.insertContent(str); //'Title: ' + e.data.title + ' ' + e.data.label);
                }
            });
        }
    });
    
    
    // Adds a menu item to the tools menu
    editor.addMenuItem('linknonpublic', {
        text: 'Seiten',
        onclick: function() {
            // Open window with a specific url
            editor.windowManager.open({
                title: 'TinyMCE site',
                url: 'http://www.tinymce.com',
                width: 800,
                height: 600,
                buttons: [{
                    text: 'Close',
                    onclick: 'close'
                }]
            });
        }
    });
});